import React, { useEffect, useRef } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import './Home.css';

export default function Home() {
  const mapRef = useRef(null);

  useEffect(() => {
    // Initialize the map
    if (mapRef.current) {
      const map = L.map(mapRef.current).setView([51.505, -0.09], 13);

      // Add tile layer
      L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      // Handle click event
      map.on('click', (e) => {
        const { lat, lng } = e.latlng; // Get latitude and longitude from the event
        L.popup()
          .setLatLng([lat, lng]) // Set the popup location
          .setContent(`You clicked at latitude: ${lat}, longitude: ${lng}`) // Set the content
          .openOn(map); // Open the popup on the map
      });
    }
  }, []);

  return (
    <>
      <div className="header">Click on the map to select a location!</div>
      <div id="map" ref={mapRef} className="map-container"></div>
    </>
  );
}
