import React, { useEffect, useRef, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-routing-machine'; // Import the Routing plugin
import './Home.css'; // Ensure this file contains relevant styles

export default function Home() {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [routingControl, setRoutingControl] = useState(null);
  const [currentLocation, setCurrentLocation] = useState('');
  const [destination, setDestination] = useState('');

  useEffect(() => {
    // Initialize the map
    if (mapRef.current && !map) {
      const newMap = L.map(mapRef.current).setView([51.505, -0.09], 13);

      L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(newMap);

      setMap(newMap); // Store the map instance in state
    }
  }, [map]);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Ensure the routing control is removed before adding a new one
    if (routingControl) {
      map.removeControl(routingControl);
    }

    // Parse user inputs to latitude and longitude (requires a geocoding service in a real-world scenario)
    const [startLat, startLng] = currentLocation.split(',').map((coord) => parseFloat(coord.trim()));
    const [destLat, destLng] = destination.split(',').map((coord) => parseFloat(coord.trim()));

    // Add a new routing control
    const newRoutingControl = L.Routing.control({
      waypoints: [
        L.latLng(startLat, startLng),
        L.latLng(destLat, destLng),
      ],
      routeWhileDragging: true,
    }).addTo(map);

    setRoutingControl(newRoutingControl); // Store the routing control instance in state
  };

  return (
    <>
      <div className="header">Enter Locations to See Route on Map</div>
      <div id="map" ref={mapRef} className="map-container"></div>
      <form onSubmit={handleFormSubmit}>
        <div style={{ display: 'flex', gap: '10px', margin: '10px 0' }}>
          <input
            type="text"
            placeholder="Enter Your Location (e.g., 51.505, -0.09)"
            value={currentLocation}
            onChange={(e) => setCurrentLocation(e.target.value)} // Update state on change
            id="curlocation"
          />
          <input
            type="text"
            placeholder="Enter Destination (e.g., 51.515, -0.10)"
            value={destination}
            onChange={(e) => setDestination(e.target.value)} // Update state on change
            id="destination"
          />
        </div>
        <input type="submit" value="Show Route" />
      </form>
    </>
  );
}
