import React, { useEffect, useRef } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet'; // Import Leaflet
import './Home.css'; // Ensure this file contains relevant styles

export default function Home() {
    var container = L.DomUtil.get('map'); if(container != null){ container._leaflet_id = null; }
    const mapRef = useRef(null);

  useEffect(() => {
    // Check if the map is already initialized
    if (mapRef.current) {
      var map = L.map(mapRef.current).setView([51.505, -0.09], 13);

      L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      L.marker([51.5, -0.09])
        .addTo(map)
        .bindPopup('You Stand Here!')
        .openPopup();
    }
    map.innerHtML='<i class="fas fa-map-marker-alt"></i>';
    
  }, []); // Empty dependency array ensures this runs once after component mounts
  let curloc = document.getElementById("curlocation");
  let direc = document.getElementById("destinantion");
  console.log("Your Location",curloc,"Diresctin",direc);

  return (
    <>
      <div className="header">
        Hello
      </div>
      <div id="map" ref={mapRef} className="map-container"></div>
      <form>
      <div style={{'display':'flex'}}>
        
        <div><input type='text'placeholder='Enter Your Location' id='curlocation'/></div>
        <div><input type='text'placeholder='Enter Destinationn' id='destinantion'/></div>
        
      </div>
      </form>
    </>
  );
}
