import React, { useEffect, useRef, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet'; // Import Leaflet
import './Home.css'; // Ensure this file contains relevant styles

export default function Home() {
  const mapRef = useRef(null);
  const [currentLocation, setCurrentLocation] = useState('');
  const [destination, setDestination] = useState('');

  useEffect(() => {
    // Ensure Leaflet map is initialized only once
    if (mapRef.current) {
      const map = L.map(mapRef.current).setView([51.505, -0.09], 13);

      L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      L.marker([51.5, -0.09])
        .addTo(map)
        .bindPopup('You Stand Here!')
        .openPopup();
    }
  }, []); // Empty dependency array ensures this runs once after component mounts

  // Form submission handler
  const handleFormSubmit = (e) => {
    e.preventDefault(); // Prevent form from refreshing the page
    console.log('Your Location:', currentLocation, 'Destination:', destination);
  };

  return (
    <>
      <div className="header">Hello</div>
      <div id="map" ref={mapRef} className="map-container"></div>
      <form onSubmit={handleFormSubmit}>
        <div style={{ display: 'flex', gap: '10px', margin: '10px 0' }}>
          <input
            type="text"
            placeholder="Enter Your Location"
            value={currentLocation}
            onChange={(e) => setCurrentLocation(e.target.value)} // Update state on change
            id="curlocation"
          />
          <input
            type="text"
            placeholder="Enter Destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)} // Update state on change
            id="destination"
          />
        </div>
        <input type="submit"/>
      </form>
    </>
  );
}
