import React, { useEffect, useRef, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-routing-machine';
import axios from 'axios'; // For API requests
import './Home.css';

export default function Home() {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [routingControl, setRoutingControl] = useState(null);
  const [currentLocation, setCurrentLocation] = useState('');
  const [destination, setDestination] = useState('');
  const [error, setError] = useState('');

  const GEOCODE_API_KEY = 'b6c761d3da3f4599aa4fc4637807e570'; // Replace with your OpenCage API Key

  useEffect(() => {
    if (!map && mapRef.current) {
      const newMap = L.map(mapRef.current).setView([20.5937, 78.9629], 5); // Center map on India
      L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(newMap);
      setMap(newMap);
    }
  }, [map]);

  const geocodeCity = async (city) => {
    try {
      const response = await axios.get(
        `https://api.opencagedata.com/geocode/v1/json?q=${encodeURIComponent(city)}&key=${GEOCODE_API_KEY}`
      );
      const { results } = response.data;
      if (results && results.length > 0) {
        const { lat, lng } = results[0].geometry;
        return [lat, lng]; // Return latitude and longitude
      } else {
        throw new Error(`No results found for "${city}".`);
      }
    } catch (error) {
      console.error(`Geocoding error for "${city}":`, error.message);
      setError(`Failed to find location for "${city}". Please try again.`);
      return null;
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors

    const startCoords = await geocodeCity(currentLocation);
    const destCoords = await geocodeCity(destination);

    if (startCoords && destCoords) {
      if (routingControl) {
        map.removeControl(routingControl); // Remove the previous route if it exists
      }

      const newRoutingControl = L.Routing.control({
        waypoints: [L.latLng(startCoords[0], startCoords[1]), L.latLng(destCoords[0], destCoords[1])],
        routeWhileDragging: true,
        createMarker: function (i, waypoint) {
          return L.marker(waypoint.latLng);
        },
        show: false, // Hide turn-by-turn directions
      }).addTo(map);

      setRoutingControl(newRoutingControl);
    }
  };

  return (
    <>
      <div className="header">City-to-City Routing</div>
      <div id="map" ref={mapRef} className="map-container"></div>
      <form onSubmit={handleFormSubmit}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', margin: '10px 0' }}>
          <input
            type="text"
            placeholder="Enter Your City (e.g., Mumbai)"
            value={currentLocation}
            onChange={(e) => setCurrentLocation(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Destination City (e.g., Delhi)"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
          <input type="submit" value="Show Route" />
        </div>
      </form>
      {error && <div style={{ color: 'red', marginTop: '10px' }}>{error}</div>}
    </>
  );
}
